    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try
        {
            int width   = Integer.parseInt( m_widthTextField.getText() );
            int height  = Integer.parseInt( m_heightTextField.getText() );
            int originX = Integer.parseInt( m_originXTextField.getText() );
            int originY = Integer.parseInt( m_originYTextField.getText() );

            if ( width <= 0 )
            {
                JOptionPane.showMessageDialog( this,
                    "Invalid screen width", "Error",
                    JOptionPane.ERROR_MESSAGE );
                return;
            }

            if ( width > 16383 )
            {
                JOptionPane.showMessageDialog( this,
                    "Screen width is too large", "Error",
                    JOptionPane.ERROR_MESSAGE );
                return;
            }

            if ( height <= 0 )
            {
                JOptionPane.showMessageDialog( this,
                    "Invalid screen height", "Error",
                    JOptionPane.ERROR_MESSAGE );
                return;
            }

            if ( height > 16383 )
            {
                JOptionPane.showMessageDialog( this,
                    "Screen height is too large", "Error",
                    JOptionPane.ERROR_MESSAGE );
                return;
            }

            m_displayedSurface.setOrigin( originX, originY );
            m_displayedSurface.setDisplayedSize( width, height );
            this.dispose();
        } catch ( NumberFormatException e ) {
            JOptionPane.showMessageDialog( this,
                "Invalid number", "Error",
                JOptionPane.ERROR_MESSAGE );
        }
    }//GEN-LAST:event_jButton1ActionPerformed

