    private void importData()
    {
        m_widthTextField.setText(
            String.valueOf( m_displayedSurface.getDisplayedWidth() ) );
        m_heightTextField.setText(
            String.valueOf( m_displayedSurface.getDisplayedHeight() ) );
        m_originXTextField.setText(
            String.valueOf( m_displayedSurface.getOriginX() ) );
        m_originYTextField.setText(
            String.valueOf( m_displayedSurface.getOriginY() ) );
    }

    private SceneDisplaySurface m_displayedSurface;

